, 3600, NULL, '', 3, 0, NULL, 0, 0, '', 0, NULL, 0, 0, NULL, NULL, 0, NULL, '', NULL, 0, 0, 1, 'cal_month', 0, 'ascending', 0, 0, '', '', 0, 'ascending', 0, 0, 0, 0, '', NULL, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, '', '', NULL, 'all_items', '', 0, '', 'news_month', 0, 'order_date_desc', 0, NULL, NULL, NULL, 0, NULL, NULL, '', 'all_items', NULL, NULL, 0, 0, 0, 0, 0, 0, '<dialog class=\"dialog--info card\">\n  <div class=\"dialog__header\">\n    <h3>Moin! 👋</h3>\n    <button class=\"dialog__close\">✕</button>\n  </div>\n  <p>\n    Möchtest du wissen, wie du OPTIMIST an Deine Vorstellungen anpasst? Dann melde dich kostenlos für den Kurs <a href=\"https://erdmann-freunde.de/optimist-kurs\">Bessere Websites mit OPTIMIST</a> an:\n  </p>\n  <div class=\"btn btn--primary\">\n    <a href=\"https://erdmann-freunde.de/optimist-kurs\">Zur Anmeldung</a>\n  </div>\n</dialog>\n<style>\n  @keyframes slide-in {\n    0% {\n      transform: translateY(120%); /* Startet außerhalb des Bildschirms */\n    }\n    100% {\n      transform: translateY(0); /* Kommt in den sichtbaren Bereich */\n    }\n  }\n  .dialog--info {\n    position: fixed;\n    inset: auto var(--base-spacing-unit)  var(--base-spacing-unit) auto;\n    margin: 0;\n    max-width: 100%;\n    width: 26rem;\n    box-shadow: var(--card-shadow);\n    display: none;\n  }\n  .dialog--info[open] {\n    display: block;\n    transform: translateY(120%);\n    animation: slide-in 0.5s 10s forwards;\n  }\n  .dialog__header {\n    display: flex;\n    justify-content: space-between;\n    align-items: flex-start;\n  }\n  .dialog__close {\n    background: transparent;\n    border: none;\n    padding: 0;\n    outline: none;\n  }\n</style>\n<script>\n  const banner = document.querySelector(\'.dialog--info\');\n  const closeBannerButton = document.querySelector(\'.dialog__close\');\n\n  // Funktion zum Anzeigen des Dialogs\n  const showBanner = () => {\n   banner.show();\n  };\n\n  // Überprüfen, ob das Banner bereits geschlossen wurde\n  const lastClosed = localStorage.getItem(\'bannerClosedAt\');\n\n  if (!lastClosed) {\n    showBanner();\n  } else {\n    const now = new Date();\n    const closedAt = new Date(lastClosed);\n    const daysPassed = (now - closedAt) / (1000 * 60 * 60 * 24);\n\n    if (daysPassed >= 3) {\n      showBanner();\n    }\n  }\n\n  // Wenn der Benutzer das Banner schließt\n  closeBannerButton.addEventListener(\'click\', () => {\n    console.log(\'close\')\n    banner.close();\n    localStorage.setItem(\'bannerClosedAt\', new Date().toISOString());\n  });\n\n</script>', '', NULL);
